import React from 'react';
import MicIcon from '@mui/icons-material/Mic';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';
import ThumbUpIcon from '@mui/icons-material/ThumbUp';
import ThumbDownAltIcon from '@mui/icons-material/ThumbDownAlt';
import './ChatScreen.css';

const ChatScreen = ({
  messages,
  inputValue,
  setInputValue,
  isListening,
  isMuted,
  isSidebarOpen,
  setIsSidebarOpen,
  handleKeyPress,
  toggleMic,
  sendMessage,
  speakMessage,
  copyMessageText,
  regenerateResponse,
  toggleFeedback,
  messageFeedback,
  createNewChat,
  clearChat,
  chatSessions,
  setIsMuted
}) => {
  // Group chat sessions by date
  const groupSessionsByDate = () => {
    const today = new Date();
    const yesterday = new Date(today);
    yesterday.setDate(yesterday.getDate() - 1);
    
    const todaySessions = [];
    const yesterdaySessions = [];
    const previousSessions = [];
    
    chatSessions.forEach(session => {
      const sessionDate = new Date(session.timestamp);
      
      if (sessionDate.toDateString() === today.toDateString()) {
        todaySessions.push(session);
      } else if (sessionDate.toDateString() === yesterday.toDateString()) {
        yesterdaySessions.push(session);
      } else {
        previousSessions.push(session);
      }
    });
    
    return { todaySessions, yesterdaySessions, previousSessions };
  };

  const { todaySessions, yesterdaySessions, previousSessions } = groupSessionsByDate();

  return (
    <>
      {/* Sidebar */}
      <div className={`sidebar ${isSidebarOpen ? 'open' : ''}`}>
        <div className="sidebar-header">
          <button className="new-chat-btn" onClick={createNewChat}>
            + New chat
          </button>
        </div>
        <div className="sidebar-content">
          <div className="history-section">
            {todaySessions.length > 0 && (
              <div className="history-group">
                <div className="history-group-title">Today</div>
                {todaySessions.map(session => (
                  <div key={session.id} className="history-item">
                    <div className="history-item-content">
                      ü§ñ {session.title || "New Chat"}
                    </div>
                  </div>
                ))}
              </div>
            )}
            
            {yesterdaySessions.length > 0 && (
              <div className="history-group">
                <div className="history-group-title">Yesterday</div>
                {yesterdaySessions.map(session => (
                  <div key={session.id} className="history-item">
                    <div className="history-item-content">
                      ü§ñ {session.title || "New Chat"}
                    </div>
                  </div>
                ))}
              </div>
            )}
            
            {previousSessions.length > 0 && (
              <div className="history-group">
                <div className="history-group-title">Previous 7 Days</div>
                {previousSessions.map(session => (
                  <div key={session.id} className="history-item">
                    <div className="history-item-content">
                      ü§ñ {session.title || "New Chat"}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
        <div className="sidebar-footer">
          <div className="sidebar-footer-content">
            <div className="footer-item">
              <span className="footer-icon">üóëÔ∏è</span>
              <span className="footer-text" onClick={clearChat}>Clear conversations</span>
            </div>
            <div className="footer-item">
              <span className="footer-icon">üìä</span>
              <a href="/admin" className="footer-text">Dashboard</a>
            </div>
            <div className="footer-item">
              <span className="footer-icon">‚öôÔ∏è</span>
              <span className="footer-text">Settings</span>
            </div>
            <div className="user-section">
              <div className="user-info">
                <div className="user-avatar">U</div>
                <div className="user-name">User</div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Overlay for mobile */}
      {isSidebarOpen && <div className="sidebar-overlay" onClick={() => setIsSidebarOpen(false)}></div>}

      <div className="chat-wrapper">
        <header className="chat-header">
          <button 
            className="sidebar-toggle" 
            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
          >
            ‚ò∞
          </button>
          <div className="brand">
            <div className="logo">ü§ñ</div>
            <h1>YVI Tech Assistant</h1>
          </div>
          <div className="header-controls">
            <button 
              className={`mute-toggle ${isMuted ? 'muted' : ''}`}
              onClick={() => setIsMuted(!isMuted)}
              title={isMuted ? "Unmute Voice Output" : "Mute Voice Output"}
            >
              {isMuted ? 'üîá' : 'üîä'}
            </button>
            <button className="clear-btn" onClick={clearChat}>Clear</button>
          </div>
        </header>

        <main className="chat-window">
          {messages.length === 0 ? (
            <>
              {/* Centered search bar for initial state */}
              <div className="initial-search-container">
                {/* Search bar in the center for initial state */}
                <div className="centered-search-bar">
                  <div className="input-container">
                    <textarea
                      value={inputValue}
                      onChange={(e) => setInputValue(e.target.value)}
                      onKeyPress={handleKeyPress}
                      placeholder="Message YVI Tech Assistant..."
                      rows="1"
                    />
                    <button 
                      className={`mic-btn ${isListening ? 'listening' : ''}`}
                      onClick={toggleMic}
                      title="Start Voice Input (Ctrl+M)"
                    >
                      <MicIcon />
                    </button>
                    <button onClick={sendMessage}>‚û§</button>
                  </div>
                </div>
                
                {/* Prompt section above the search bar */}
                <div className="prompt-section">
                  <div className="prompt-questions">
                    <div className="prompt-question" onClick={() => setInputValue("What services do you offer?")}>Services</div>
                    <div className="prompt-question" onClick={() => setInputValue("Tell me about Oracle HCM")}>Oracle HCM</div>
                    <div className="prompt-question" onClick={() => setInputValue("How does your development process work?")}>Process</div>
                  </div>
                </div>
              </div>
            </>
          ) : (
            <>
              {messages.map(message => (
                <MessageBubble 
                  key={message.id} 
                  message={message} 
                  isMuted={isMuted}
                  onReplay={speakMessage}
                  onCopy={() => copyMessageText(message.id)}
                  onRegenerate={() => regenerateResponse(message.id)}
                  onFeedback={(feedbackType) => toggleFeedback(message.id, feedbackType)}
                  feedback={messageFeedback[message.id]}
                />
              ))}
              
              {/* Prompt section above the search bar */}
              <div className="prompt-section">
                <div className="prompt-questions">
                  <div className="prompt-question" onClick={() => setInputValue("What services do you offer?")}>Services</div>
                  <div className="prompt-question" onClick={() => setInputValue("Tell me about Oracle HCM")}>Oracle HCM</div>
                  <div className="prompt-question" onClick={() => setInputValue("How does your development process work?")}>Process</div>
                </div>
              </div>
              
              <footer className="chat-input-bar">
                <div className="input-container">
                  <textarea
                    value={inputValue}
                    onChange={(e) => setInputValue(e.target.value)}
                    onKeyPress={handleKeyPress}
                    placeholder="Message YVI Tech Assistant..."
                    rows="1"
                  />
                  <button 
                    className={`mic-btn ${isListening ? 'listening' : ''}`}
                    onClick={toggleMic}
                    title="Start Voice Input (Ctrl+M)"
                  >
                    <MicIcon />
                  </button>
                  <button onClick={sendMessage}>‚û§</button>
                </div>
              </footer>
            </>
          )}
        </main>
        
        {isListening && (
          <div className="listening-indicator">
            <span>üé§ Listening...</span>
          </div>
        )}
      </div>
    </>
  );
};

function MessageBubble({ message, isMuted, onReplay, onCopy, onRegenerate, onFeedback, feedback }) {
  if (message.isLoading) {
    return (
      <div className="message bot">
        <div className="avatar">AI</div>
        <div className="message-content">
          <div className="message-text">
            <div className="typing-indicator">
              <span></span>
              <span></span>
              <span></span>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className={`message ${message.sender}`}>
      <div className="avatar">
        {message.sender === 'user' ? 'U' : 'AI'}
        {message.sender === 'bot' && !isMuted && (
          <button 
            className="replay-btn"
            onClick={() => onReplay(message.text)}
            title="Replay message audio"
          >
            üîä
          </button>
        )}
      </div>
      <div className="message-content">
        <div className="message-text">{message.text}</div>
        {message.sender === 'bot' && (
          <div className="message-actions">
            <button className="action-btn" onClick={onCopy} title="Copy">
              <ContentCopyIcon />
            </button>
            <button className="action-btn" onClick={onRegenerate} title="Regenerate">
              ‚Üª
            </button>
            <div className="feedback-buttons">
              <button 
                className={`thumb-btn ${feedback === 'up' ? 'active' : ''}`}
                onClick={() => onFeedback('up')}
                title="Thumbs up"
              >
                <ThumbUpIcon />
              </button>
              <button 
                className={`thumb-btn ${feedback === 'down' ? 'active' : ''}`}
                onClick={() => onFeedback('down')}
                title="Thumbs down"
              >
                <ThumbDownAltIcon />
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default ChatScreen;
