import React, { useState } from 'react';
import MicIcon from '@mui/icons-material/Mic';
import './StartChat.css';

const StartChat = ({ onSendMessage }) => {
  const [inputValue, setInputValue] = useState('');
  const [isListening, setIsListening] = useState(false);

  const handleSendMessage = () => {
    if (inputValue.trim()) {
      onSendMessage(inputValue);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const toggleMic = () => {
    // In a real implementation, this would integrate with speech recognition
    alert('Voice input would be activated in a full implementation');
  };

  return (
    <div className="start-chat-screen">
      <header className="start-chat-header">
        <div className="brand">
          <div className="logo">ðŸ¤–</div>
          <h1>YVI Tech Assistant</h1>
        </div>
      </header>

      <main className="start-chat-main">
        <div className="welcome-section">
          <h2>ðŸ‘‹ Welcome to YVI Tech Assistant</h2>
          <p>Ask about our <strong>Services</strong>, <strong>Capabilities</strong>, or <strong>Process</strong>.</p>
        </div>

        <div className="quick-prompts">
          <h3>Try asking about:</h3>
          <div className="prompt-buttons">
            <button className="prompt-btn" onClick={() => setInputValue("What services do you offer?")}>Services</button>
            <button className="prompt-btn" onClick={() => setInputValue("Tell me about Oracle HCM")}>Oracle HCM</button>
            <button className="prompt-btn" onClick={() => setInputValue("How does your development process work?")}>Process</button>
          </div>
        </div>

        <div className="start-chat-container">
          <div className="input-container">
            <textarea
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Message YVI Tech Assistant..."
              rows="1"
            />
            <button 
              className={`mic-btn ${isListening ? 'listening' : ''}`}
              onClick={toggleMic}
              title="Start Voice Input (Ctrl+M)"
            >
              <MicIcon />
            </button>
            <button onClick={handleSendMessage}>âž¤</button>
          </div>
        </div>
      </main>
    </div>
  );
};

export default StartChat;
