import React, { useState, useEffect, useRef } from 'react';
import './App.css';

function App() {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [isListening, setIsListening] = useState(false);
  const [isMuted, setIsMuted] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const chatWindowRef = useRef(null);
  const recognitionRef = useRef(null);

  // Load chat history from localStorage on component mount
  useEffect(() => {
    const savedMessages = localStorage.getItem('chatMessages');
    if (savedMessages) {
      setMessages(JSON.parse(savedMessages));
    }
    
    const savedMuteState = localStorage.getItem('isMuted') === 'true';
    setIsMuted(savedMuteState);
  }, []);

  // Save messages to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('chatMessages', JSON.stringify(messages));
  }, [messages]);

  // Save mute state to localStorage
  useEffect(() => {
    localStorage.setItem('isMuted', isMuted);
  }, [isMuted]);

  // Scroll to bottom of chat window
  useEffect(() => {
    if (chatWindowRef.current) {
      chatWindowRef.current.scrollTop = chatWindowRef.current.scrollHeight;
    }
  }, [messages]);

  // Keyboard shortcuts
  useEffect(() => {
    const handleKeyDown = (e) => {
      // Ctrl + M to toggle microphone
      if (e.ctrlKey && e.key === 'm') {
        e.preventDefault();
        toggleMic();
      }
      
      // Escape to close sidebar
      if (e.key === 'Escape' && isSidebarOpen) {
        setIsSidebarOpen(false);
      }
    };
    
    window.addEventListener('keydown', handleKeyDown);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [isSidebarOpen]);

  // Initialize speech recognition
  useEffect(() => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    
    if (SpeechRecognition) {
      recognitionRef.current = new SpeechRecognition();
      recognitionRef.current.lang = 'en-US';
      recognitionRef.current.continuous = false;
      recognitionRef.current.interimResults = true;
      
      recognitionRef.current.onstart = () => {
        setIsListening(true);
      };
      
      recognitionRef.current.onresult = (event) => {
        let interimTranscript = '';
        let finalTranscript = '';
        
        for (let i = event.resultIndex; i < event.results.length; i++) {
          const transcript = event.results[i][0].transcript;
          if (event.results[i].isFinal) {
            finalTranscript += transcript + ' ';
          } else {
            interimTranscript += transcript;
          }
        }
        
        setInputValue(finalTranscript + interimTranscript);
      };
      
      recognitionRef.current.onerror = (event) => {
        console.error('Speech recognition error', event.error);
        setIsListening(false);
      };
      
      recognitionRef.current.onend = () => {
        setIsListening(false);
        if (inputValue.trim() !== '') {
          sendMessage();
        }
      };
    }
  }, [inputValue]);

  const toggleMic = () => {
    if (!recognitionRef.current) {
      alert('Voice input not supported in this browser');
      return;
    }
    
    if (isListening) {
      recognitionRef.current.stop();
    } else {
      try {
        recognitionRef.current.start();
      } catch (error) {
        console.error('Error starting speech recognition:', error);
        alert('Error starting voice input');
      }
    }
  };

  const sendMessage = async () => {
    if (!inputValue.trim()) return;

    // Add user message
    const userMessage = {
      id: Date.now(),
      text: inputValue,
      sender: 'user',
      timestamp: new Date().toISOString()
    };

    setMessages(prev => [...prev, userMessage]);
    const userText = inputValue;
    setInputValue('');

    // Show loading indicator
    const loadingMessage = {
      id: Date.now() + 1,
      text: '',
      sender: 'bot',
      isLoading: true,
      timestamp: new Date().toISOString()
    };

    setMessages(prev => [...prev, loadingMessage]);

    try {
      // Send message to backend
      const response = await fetch('/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message: userText.toLowerCase() }),
      });

      const data = await response.json();

      // Remove loading indicator and add bot response
      setMessages(prev => {
        const withoutLoading = prev.filter(msg => !msg.isLoading);
        const botMessage = {
          id: Date.now() + 2,
          text: `${data.title}: ${data.answer}`,
          sender: 'bot',
          timestamp: new Date().toISOString()
        };
        return [...withoutLoading, botMessage];
      });

      // Speak the response if not muted
      if (!isMuted) {
        speakMessage(`${data.title}: ${data.answer}`);
      }
    } catch (error) {
      // Remove loading indicator and show error
      setMessages(prev => {
        const withoutLoading = prev.filter(msg => !msg.isLoading);
        const errorMessage = {
          id: Date.now() + 2,
          text: 'Error: Sorry, I encountered an issue processing your request. Please try again.',
          sender: 'bot',
          timestamp: new Date().toISOString()
        };
        return [...withoutLoading, errorMessage];
      });
    }
  };

  const speakMessage = (text) => {
    if (isMuted || !window.speechSynthesis) return;
    
    // Cancel any ongoing speech
    window.speechSynthesis.cancel();
    
    const utter = new SpeechSynthesisUtterance(text);
    utter.lang = 'en-US';
    utter.rate = 1.0;
    utter.pitch = 1.0;
    window.speechSynthesis.speak(utter);
  };

  const toggleMute = () => {
    setIsMuted(!isMuted);
    if (!isMuted) {
      // Mute - cancel any ongoing speech
      window.speechSynthesis?.cancel();
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const clearChat = () => {
    if (window.confirm('Are you sure you want to clear the chat history?')) {
      setMessages([]);
      localStorage.removeItem('chatMessages');
    }
  };

  const createNewChat = () => {
    if (messages.length > 0) {
      if (window.confirm('Start a new chat? This will clear the current conversation.')) {
        setMessages([]);
        localStorage.removeItem('chatMessages');
      }
    }
  };

  return (
    <div className="app">
      {/* Sidebar */}
      <div className={`sidebar ${isSidebarOpen ? 'open' : ''}`}>
        <div className="sidebar-header">
          <h2>YVI Assistant</h2>
        </div>
        <div className="sidebar-content">
          <button className="sidebar-btn" onClick={createNewChat}>üÜï New Chat</button>
          <button className="sidebar-btn">üìú Chat History</button>
          <button className="sidebar-btn" onClick={clearChat}>üßπ Clear All</button>
          <a href="/admin" className="sidebar-btn">üìä Dashboard</a>
          <button className="sidebar-btn">‚öôÔ∏è Settings</button>
        </div>
      </div>

      {/* Overlay for mobile */}
      {isSidebarOpen && <div className="sidebar-overlay" onClick={() => setIsSidebarOpen(false)}></div>}

      <div className="chat-wrapper">
        <header className="chat-header">
          <button 
            className="sidebar-toggle" 
            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
          >
            ‚ò∞
          </button>
          <div className="brand">
            <div className="logo">ü§ñ</div>
            <h1>YVI Tech Assistant</h1>
          </div>
          <div className="header-controls">
            <button 
              className={`mute-toggle ${isMuted ? 'muted' : ''}`}
              onClick={toggleMute}
              title={isMuted ? "Unmute Voice Output" : "Mute Voice Output"}
            >
              {isMuted ? 'üîá' : 'üîä'}
            </button>
            <button className="clear-btn" onClick={clearChat}>Clear</button>
          </div>
        </header>

        <main className="chat-window" ref={chatWindowRef}>
          {messages.length === 0 ? (
            <div className="welcome">
              <h2>üëã Welcome to YVI Tech Assistant</h2>
              <p>Ask about our <strong>Services</strong>, <strong>Capabilities</strong>, or <strong>Process</strong>.</p>
              <div className="quick-prompts">
                <button onClick={() => setInputValue("What services do you offer?")}>What services do you offer?</button>
                <button onClick={() => setInputValue("Tell me about Oracle HCM")}>Tell me about Oracle HCM</button>
                <button onClick={() => setInputValue("How does your development process work?")}>Development process</button>
              </div>
            </div>
          ) : (
            messages.map(message => (
              <MessageBubble 
                key={message.id} 
                message={message} 
                isMuted={isMuted}
                onReplay={speakMessage}
              />
            ))
          )}
        </main>

        <footer className="chat-input-bar">
          <textarea
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Message YVI Tech Assistant..."
            rows="1"
          />
          <div className="input-controls">
            <button 
              className={`mic-btn ${isListening ? 'listening' : ''}`}
              onClick={toggleMic}
              title="Start Voice Input (Ctrl+M)"
            >
              üé§
            </button>
            <button onClick={sendMessage}>‚û§</button>
          </div>
        </footer>
        
        {isListening && (
          <div className="listening-indicator">
            <span>üé§ Listening...</span>
          </div>
        )}
      </div>
    </div>
  );
}

function MessageBubble({ message, isMuted, onReplay }) {
  if (message.isLoading) {
    return (
      <div className="message bot">
        <div className="avatar">AI</div>
        <div className="message-content">
          <div className="message-text">
            <div className="typing-indicator">
              <span></span>
              <span></span>
              <span></span>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className={`message ${message.sender}`}>
      <div className="avatar">
        {message.sender === 'user' ? 'U' : 'AI'}
        {message.sender === 'bot' && !isMuted && (
          <button 
            className="replay-btn"
            onClick={() => onReplay(message.text)}
            title="Replay message audio"
          >
            üîä
          </button>
        )}
      </div>
      <div className="message-content">
        <div className="message-text">{message.text}</div>
      </div>
    </div>
  );
}

export default App;
