import React, { useState, useEffect, useRef } from 'react';
import MicIcon from '@mui/icons-material/Mic';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';
import ThumbUpIcon from '@mui/icons-material/ThumbUp';
import ThumbDownAltIcon from '@mui/icons-material/ThumbDownAlt';
import GuestScreen from './components/GuestScreen';
import StartChat from './components/StartChat';
import ChatScreen from './components/ChatScreen';
import './App.css';

function App() {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [isListening, setIsListening] = useState(false);
  const [isMuted, setIsMuted] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [chatSessions, setChatSessions] = useState([]);
  const [currentSessionId, setCurrentSessionId] = useState(null);
  const [messageFeedback, setMessageFeedback] = useState({});
  const [currentView, setCurrentView] = useState('guest'); // 'guest', 'start', or 'chat'
  const chatWindowRef = useRef(null);
  const recognitionRef = useRef(null);

  // Load chat history from localStorage on component mount
  useEffect(() => {
    const savedMessages = localStorage.getItem('chatMessages');
    const savedSessions = localStorage.getItem('chatSessions');
    const savedFeedback = localStorage.getItem('messageFeedback');
    const savedMuteState = localStorage.getItem('isMuted') === 'true';
    
    if (savedMessages) {
      setMessages(JSON.parse(savedMessages));
    }
    
    if (savedSessions) {
      setChatSessions(JSON.parse(savedSessions));
    }
    
    if (savedFeedback) {
      setMessageFeedback(JSON.parse(savedFeedback));
    }
    
    setIsMuted(savedMuteState);
  }, []);

  // Save messages to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('chatMessages', JSON.stringify(messages));
  }, [messages]);

  // Save sessions to localStorage
  useEffect(() => {
    localStorage.setItem('chatSessions', JSON.stringify(chatSessions));
  }, [chatSessions]);

  // Save feedback to localStorage
  useEffect(() => {
    localStorage.setItem('messageFeedback', JSON.stringify(messageFeedback));
  }, [messageFeedback]);

  // Save mute state to localStorage
  useEffect(() => {
    localStorage.setItem('isMuted', isMuted);
  }, [isMuted]);

  // Scroll to bottom of chat window
  useEffect(() => {
    if (chatWindowRef.current) {
      chatWindowRef.current.scrollTop = chatWindowRef.current.scrollHeight;
    }
  }, [messages]);

  // Keyboard shortcuts
  useEffect(() => {
    const handleKeyDown = (e) => {
      // Ctrl + M to toggle microphone
      if (e.ctrlKey && e.key === 'm') {
        e.preventDefault();
        toggleMic();
      }
      
      // Escape to close sidebar
      if (e.key === 'Escape' && isSidebarOpen) {
        setIsSidebarOpen(false);
      }
    };
    
    window.addEventListener('keydown', handleKeyDown);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [isSidebarOpen]);

  // Initialize speech recognition
  useEffect(() => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    
    if (SpeechRecognition) {
      recognitionRef.current = new SpeechRecognition();
      recognitionRef.current.lang = 'en-US';
      recognitionRef.current.continuous = false;
      recognitionRef.current.interimResults = true;
      
      recognitionRef.current.onstart = () => {
        setIsListening(true);
      };
      
      recognitionRef.current.onresult = (event) => {
        let interimTranscript = '';
        let finalTranscript = '';
        
        for (let i = event.resultIndex; i < event.results.length; i++) {
          const transcript = event.results[i][0].transcript;
          if (event.results[i].isFinal) {
            finalTranscript += transcript + ' ';
          } else {
            interimTranscript += transcript;
          }
        }
        
        setInputValue(finalTranscript + interimTranscript);
      };
      
      recognitionRef.current.onerror = (event) => {
        console.error('Speech recognition error', event.error);
        setIsListening(false);
      };
      
      recognitionRef.current.onend = () => {
        setIsListening(false);
        if (inputValue.trim() !== '') {
          sendMessage();
        }
      };
    }
  }, [inputValue]);

  const toggleMic = () => {
    if (!recognitionRef.current) {
      alert('Voice input not supported in this browser');
      return;
    }
    
    if (isListening) {
      recognitionRef.current.stop();
    } else {
      try {
        recognitionRef.current.start();
      } catch (error) {
        console.error('Error starting speech recognition:', error);
        alert('Error starting voice input');
      }
    }
  };

  const sendMessage = async () => {
    if (!inputValue.trim()) return;

    // Add user message
    const userMessage = {
      id: Date.now(),
      text: inputValue,
      sender: 'user',
      timestamp: new Date().toISOString()
    };

    setMessages(prev => [...prev, userMessage]);
    const userText = inputValue;
    setInputValue('');

    // Show loading indicator
    const loadingMessage = {
      id: Date.now() + 1,
      text: '',
      sender: 'bot',
      isLoading: true,
      timestamp: new Date().toISOString()
    };

    setMessages(prev => [...prev, loadingMessage]);

    try {
      // Send message to backend
      const response = await fetch('/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message: userText.toLowerCase() }),
      });

      const data = await response.json();

      // Remove loading indicator and add bot response
      setMessages(prev => {
        const withoutLoading = prev.filter(msg => !msg.isLoading);
        const botMessage = {
          id: Date.now() + 2,
          text: `${data.title}: ${data.answer}`,
          sender: 'bot',
          timestamp: new Date().toISOString()
        };
        return [...withoutLoading, botMessage];
      });

      // Speak the response if not muted
      if (!isMuted) {
        speakMessage(`${data.title}: ${data.answer}`);
      }
    } catch (error) {
      // Remove loading indicator and show error
      setMessages(prev => {
        const withoutLoading = prev.filter(msg => !msg.isLoading);
        const errorMessage = {
          id: Date.now() + 2,
          text: 'Error: Sorry, I encountered an issue processing your request. Please try again.',
          sender: 'bot',
          timestamp: new Date().toISOString()
        };
        return [...withoutLoading, errorMessage];
      });
    }
  };

  const speakMessage = (text) => {
    if (isMuted || !window.speechSynthesis) return;
    
    // Cancel any ongoing speech
    window.speechSynthesis.cancel();
    
    const utter = new SpeechSynthesisUtterance(text);
    utter.lang = 'en-US';
    utter.rate = 1.0;
    utter.pitch = 1.0;
    window.speechSynthesis.speak(utter);
  };

  const toggleMute = () => {
    setIsMuted(!isMuted);
    if (!isMuted) {
      // Mute - cancel any ongoing speech
      window.speechSynthesis?.cancel();
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const clearChat = () => {
    if (window.confirm('Are you sure you want to clear the chat history?')) {
      setMessages([]);
      localStorage.removeItem('chatMessages');
    }
  };

  const createNewChat = () => {
    if (messages.length > 0) {
      if (window.confirm('Start a new chat? This will clear the current conversation.')) {
        setMessages([]);
        localStorage.removeItem('chatMessages');
      }
    }
  };

  const copyMessageText = (messageId) => {
    const message = messages.find(msg => msg.id === messageId);
    if (message) {
      navigator.clipboard.writeText(message.text);
      // Could add a toast notification here
    }
  };

  const regenerateResponse = (messageId) => {
    // Find the message and the previous user message
    const messageIndex = messages.findIndex(msg => msg.id === messageId);
    if (messageIndex > 0) {
      const previousUserMessage = messages[messageIndex - 1];
      if (previousUserMessage.sender === 'user') {
        // Remove the bot response and regenerate
        const newMessages = messages.slice(0, messageIndex);
        setMessages(newMessages);
        
        // Simulate sending the previous message again
        setTimeout(() => {
          setInputValue(previousUserMessage.text);
          // In a real implementation, you would call sendMessage() here
        }, 100);
      }
    }
  };

  const toggleFeedback = (messageId, feedbackType) => {
    setMessageFeedback(prev => {
      const newFeedback = { ...prev };
      if (newFeedback[messageId] === feedbackType) {
        delete newFeedback[messageId];
      } else {
        newFeedback[messageId] = feedbackType;
      }
      return newFeedback;
    });
  };

  // Handle starting chat from GuestScreen
  const handleStartChat = () => {
    setCurrentView('start');
  };

  // Handle sending message from StartChat
  const handleStartSendMessage = (message) => {
    setInputValue(message);
    setCurrentView('chat');
    // Simulate sending the message
    setTimeout(() => {
      sendMessage();
    }, 100);
  };

  // Render the appropriate view based on currentView state
  const renderView = () => {
    switch (currentView) {
      case 'guest':
        return <GuestScreen onStartChat={handleStartChat} />;
      case 'start':
        return <StartChat onSendMessage={handleStartSendMessage} />;
      case 'chat':
      default:
        return (
          <ChatScreen
            messages={messages}
            inputValue={inputValue}
            setInputValue={setInputValue}
            isListening={isListening}
            isMuted={isMuted}
            isSidebarOpen={isSidebarOpen}
            setIsSidebarOpen={setIsSidebarOpen}
            handleKeyPress={handleKeyPress}
            toggleMic={toggleMic}
            sendMessage={sendMessage}
            speakMessage={speakMessage}
            copyMessageText={copyMessageText}
            regenerateResponse={regenerateResponse}
            toggleFeedback={toggleFeedback}
            messageFeedback={messageFeedback}
            createNewChat={createNewChat}
            clearChat={clearChat}
            chatSessions={chatSessions}
          />
        );
    }
  };

  return (
    <div className="app">
      {renderView()}
    </div>
  );
}

export default App;
